import React from 'react';
import { motion } from 'framer-motion';
import { 
  PlayIcon, 
  StarIcon, 
  ShoppingCartIcon,
  EyeIcon 
} from '@heroicons/react/24/solid';

const GameCard = ({ game, onViewDetails, onAddToCart }) => {
  const formatPrice = (price) => {
    return price === 0 ? 'GRATIS' : `$${price.toFixed(2)}`;
  };

  const getRatingColor = (rating) => {
    if (rating >= 9) return 'text-green-400';
    if (rating >= 7) return 'text-yellow-400';
    if (rating >= 5) return 'text-orange-400';
    return 'text-red-400';
  };

  const getPlatformIcon = (platform) => {
    if (platform.includes('PC')) return 'üñ•Ô∏è';
    if (platform.includes('PlayStation')) return 'üéÆ';
    if (platform.includes('Xbox')) return 'üéØ';
    if (platform.includes('Nintendo')) return 'üéÆ';
    if (platform.includes('Mobile')) return 'üì±';
    return 'üéÆ';
  };

  return (
    <motion.div
      className="game-card card-gaming group cursor-pointer"
      whileHover={{ y: -5 }}
      whileTap={{ scale: 0.98 }}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
    >
      <div className="relative overflow-hidden rounded-lg mb-4">
        <img
          src={game.image_url || '/api/placeholder/400/250'}
          alt={game.title}
          className="w-full h-48 object-cover transition-transform duration-300 group-hover:scale-110"
          onError={(e) => {
            e.target.src = 'https://via.placeholder.com/400x250/1f2937/6366f1?text=GameStore';
          }}
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300">
          <div className="absolute bottom-4 left-4 right-4">
            <div className="flex space-x-2">
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  onViewDetails(game);
                }}
                className="flex-1 btn-primary flex items-center justify-center space-x-2 py-2 text-sm"
              >
                <EyeIcon className="w-4 h-4" />
                <span>Ver Detalles</span>
              </button>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  onAddToCart(game);
                }}
                className="px-3 py-2 bg-green-600 hover:bg-green-700 rounded-lg transition-colors duration-300"
              >
                <ShoppingCartIcon className="w-4 h-4" />
              </button>
            </div>
          </div>
        </div>
        <div className="absolute top-2 left-2">
          <span className="bg-gaming-purple text-white px-2 py-1 rounded text-xs font-semibold">
            {game.category_name || game.genre}
          </span>
        </div>
        <div className="absolute top-2 right-2">
          <span className="text-xl">{getPlatformIcon(game.platform)}</span>
        </div>
      </div>

      <div className="space-y-3">
        <h3 className="text-xl font-bold text-white group-hover:text-gaming-cyan transition-colors duration-300 line-clamp-2">
          {game.title}
        </h3>
        
        <p className="text-gray-400 text-sm line-clamp-2">
          {game.description}
        </p>
        
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <StarIcon className="w-5 h-5 text-yellow-400" />
            <span className={`font-semibold ${getRatingColor(game.rating)}`}>
              {game.rating?.toFixed(1) || 'N/A'}
            </span>
          </div>
          <span className="text-gray-400 text-sm">
            {game.platform.split(',')[0]}
          </span>
        </div>
        
        <div className="flex items-center justify-between pt-2 border-t border-gray-700">
          <span className={`text-xl font-bold ${game.price === 0 ? 'text-green-400' : 'text-gaming-cyan'}`}>
            {formatPrice(game.price)}
          </span>
          <span className="text-gray-400 text-sm">
            {new Date(game.release_date).getFullYear()}
          </span>
        </div>
      </div>
    </motion.div>
  );
};

export default GameCard;
